import * as React from 'react'
import { connect, Connect } from '../../app'
import { Wrapper, Port, AddPort } from './elements'
import Icon from '../common/Icon'

const PortsBar: React.SFC<Connect> = ({ app }) => (
  <Wrapper>
    {Object.keys(app.state.apps).map((port) => (
      <Port
        key={port}
        onClick={() => app.actions.selectPort(port)}
        isSelected={app.state.currentPort === port}
      >
        {port}
      </Port>
    ))}
    <AddPort onClick={() => app.actions.configurePort()}>
      <Icon>plus-square</Icon>
    </AddPort>
  </Wrapper>
)

export default connect(PortsBar)
