import * as React from 'react'
import { ButtonElement } from './elements'

type Props = {
  primary: boolean
  onClick?: () => void
  type?: string
  disabled?: boolean
}

const Button: React.SFC<Props> = ({
  type,
  disabled,
  primary,
  onClick,
  children,
}) => (
  <ButtonElement
    type={type}
    disabled={disabled}
    primary={primary}
    onClick={onClick ? () => onClick() : null}
  >
    {children}
  </ButtonElement>
)

export default Button
