import * as React from 'react'
import { InputElement, Wrapper } from './elements'

type Props = {
  value: string
  onChange: (value: string) => void
  autoFocus?: boolean
  placeholder?: string
  addon?: React.ReactNode
  length?: number
}

const Input: React.SFC<Props> = ({
  value,
  onChange,
  autoFocus,
  addon = null,
  length,
  placeholder,
}) => (
  <Wrapper
    style={
      length
        ? {
            width: 75 + 16 * length + 'px',
          }
        : null
    }
  >
    <InputElement
      type="text"
      placeholder={placeholder}
      value={value}
      autoFocus={autoFocus}
      onChange={(event) => onChange(event.target.value)}
    />
    {addon}
  </Wrapper>
)

export default Input
