import * as React from 'react'
import {
  TableComponent,
  Headers,
  Header,
  Body,
  RowComponent,
  CellComponent,
} from './elements'

type Header = {
  title: string
  width: string
}

type TableProps = {
  headers: Header[]
}

const Table: React.SFC<TableProps> = ({ headers, children }) => (
  <TableComponent>
    <Headers>
      <Row>
        {headers.map((header) => (
          <Header key={header.title} width={header.width}>
            {header.title}
          </Header>
        ))}
      </Row>
    </Headers>
    <Body>{children}</Body>
  </TableComponent>
)

export const Row: React.SFC = ({ children }) => (
  <RowComponent>{children}</RowComponent>
)

type CellProps = {
  wordwrap?: string
  mono?: boolean
}

export const Cell: React.SFC<CellProps> = ({ wordwrap, mono, children }) => (
  <CellComponent wordwrap={wordwrap} mono={mono}>
    {children}
  </CellComponent>
)

export default Table
