import * as React from 'react'
import { Variant, TextElement } from './elements'

type Props = {
  variant: Variant
  dark?: boolean
  dense?: boolean
}

const Text: React.SFC<Props> = ({ dense, dark, variant, children }) => (
  <TextElement dense={dense} dark={dark} variant={variant}>
    {children}
  </TextElement>
)

export default Text
