import * as React from 'react'
import { Variant, TextElement } from './elements'
import { Colors } from '../../../theme'

type Props = {
  variant: Variant
  color?: keyof Colors
  dense?: boolean
  mono?: boolean
}

const Text: React.SFC<Props> = ({ mono, dense, color, variant, children }) => (
  <TextElement dense={dense} color={color} variant={variant} mono={mono}>
    {children}
  </TextElement>
)

export default Text
