import * as React from 'react'
import { TooltipWrapper, TooltipElement, TooltipText } from './elements'

type Props = {
  text: string
  placement?: string
}

const Tooltip: React.SFC<Props> = ({ text, children }) => (
  <TooltipWrapper>
    {children}
    <TooltipElement>
      <TooltipText>{text}</TooltipText>
    </TooltipElement>
  </TooltipWrapper>
)

export default Tooltip
