import { EventType, IAction, Overmind, SERIALIZE, createOvermindMock, rehydrate } from './'
import { namespaced } from './config'

function toJSON(obj) {
  return JSON.parse(JSON.stringify(obj))
}

class StateValue {
  value = 'foo'
  toJSON() {
    return {
      [SERIALIZE]: true,
      value: this.value
    }
  }
  static fromJSON(json: { value: string }) {
    const instance = new StateValue()
    instance.value = json.value
    return instance
  }
}

function createDefaultOvermind() {
  const state = {
    foo: 'bar',
    item: {
      isAwesome: true,
    },
    value: null as unknown as StateValue
  }
  const changeFoo: Action<void, string> = (context) => {
    context.state.foo = 'bar2'

    return 'changeFoo'
  }
  const changeFooWithEffect: Action = (context) => {
    context.state.foo = context.effects.hello()
  }
  const waitAndChangeFoo: Action<void, Promise<void>> = (context) => {
    return context.effects.wait().then(() => {
      context.state.foo = 'bar2'
    })
  }
  const asyncChangeFoo: Action<void, Promise<void>> = async (context) => {
    await Promise.resolve()
    context.state.foo = 'bar2'
  }
  const changeValue: Action<{ isAwesome: boolean }> = (_, value) => {
    value.isAwesome = !value.isAwesome
  }
  const changeFormValue: Action<{
    key: string
    form: { [key: string]: any }
    value: any
  }> = (_, payload) => {
    const { form, key, value } = payload
    form[key] = value
  }
  const rehydrateAction: Action<any> = ({ state }, newState) => {
    rehydrate(state, newState, {
      value: StateValue.fromJSON,
    })
  }
  const actions = {
    asyncChangeFoo,
    changeFormValue,
    changeFoo,
    changeFooWithEffect,
    changeValue,
    waitAndChangeFoo,
    rehydrateAction
  }
  const effects = {
    hello() {
      return 'hello'
    },
    wait() {
      return Promise.resolve()
    },
  }
  const config = {
    state,
    actions,
    effects,
  }

  type Config = typeof config

  interface Action<Value = void, ReturnValue = void | Promise<void>>
    extends IAction<Config, Value, ReturnValue> {}

  const app = new Overmind(config)

  return app
}

describe('Overmind', () => {
  test('should instantiate app with state', () => {
    const app = new Overmind({
      state: {
        foo: 'bar',
      },
    })

    expect(app.state.foo).toEqual('bar')
  })

  test('should instantiate app with onInitialize', async () => {
    expect.assertions(2)
    let value: any
    const app = new Overmind({
      onInitialize(context, val) {
        expect(context.state.foo).toBe('bar')
        value = val
      },
      state: {
        foo: 'bar',
      },
      actions: {
        doThis() {},
      },
    })
    await app.initialized
    expect(value).toBe(app)
  })
  test('should be able to type actions', () => {
    expect.assertions(2)

    const app = createDefaultOvermind()

    expect(app.state.foo).toBe('bar')
    app.actions.changeFoo()
    expect(app.state.foo).toBe('bar2')
  })
  test('should allow changing state in actions', () => {
    expect.assertions(2)
    const app = createDefaultOvermind()

    expect(app.state.foo).toBe('bar')
    app.actions.changeFoo()
    expect(app.state.foo).toBe('bar2')
  })
  test('should expose effects to actions', () => {
    expect.assertions(2)
    const app = createDefaultOvermind()

    expect(app.state.foo).toBe('bar')
    app.actions.changeFooWithEffect()
    expect(app.state.foo).toBe('hello')
  })
  test('should allow actions to return a value', () => {
    const app = createDefaultOvermind()

    expect(app.actions.changeFoo()).toBe('changeFoo')
  })
  test('should be able to do mutations async via effects', () => {
    expect.assertions(2)
    const app = createDefaultOvermind()
    expect(app.state.foo).toBe('bar')
    return app.actions.waitAndChangeFoo().then(() => {
      expect(app.state.foo).toBe('bar2')
    })
  })
  test('should track action start and end', () => {
    expect.assertions(2)
    const app = new Overmind({
      actions: {
        doThis() {},
      },
    })
    app.eventHub.once(EventType.ACTION_START, (data) => {
      expect(toJSON(data)).toEqual({
        actionId:'doThis',
        actionName: 'doThis',
        isRunning: true,
        namespacePath: [],
        executionId: 0,
        operatorId: 0,
        path: [],
        type: 'action'
      })
    })
    app.eventHub.once(EventType.ACTION_END, (data) => {
      expect(toJSON(data)).toEqual({
        actionId: 'doThis',
        isRunning: false,
        executionId: 0,
        actionName: 'doThis',
        namespacePath: [],
        operatorId: 0,
        path: [],
        type: 'action',
      })
    })
    app.actions.doThis()
  })
  test('should track operator start and end', () => {
    expect.assertions(2)
    const app = new Overmind({
      actions: {
        doThis() {},
      },
    })
    app.eventHub.once(EventType.OPERATOR_START, (data) => {
      expect(toJSON(data)).toEqual({
        actionId: 'doThis',
        isRunning: true,
        actionName: 'doThis',
        path: [],
        executionId: 0,
        operatorId: 0,
        namespacePath: [],
        type: 'action',
      })
    })
    app.eventHub.once(EventType.OPERATOR_END, (data) => {
      expect(toJSON(data)).toEqual({
        actionId: 'doThis',
        isRunning: false,
        actionName: 'doThis',
        path: [],
        isAsync: false,
        executionId: 0,
        operatorId: 0,
        namespacePath: [],
        type: 'action',
      })
    })
    app.actions.doThis()
  })
  test('should track mutations', () => {
    expect.assertions(1)
    const app = createDefaultOvermind()
    app.eventHub.once(EventType.MUTATIONS, (data) => {
      expect(toJSON(data)).toEqual({
        actionId: 'changeFoo',
        isRunning: true,
        actionName: 'changeFoo',
        mutations: [
          {
            args: ['bar2'],
            method: 'set',
            path: 'foo',
            hasChangedValue: true,
            delimiter: '.'
          },
        ],
        executionId: 0,
        operatorId: 0,
        namespacePath: [],
        path: [],
        type: 'action',
      })
    })
    app.actions.changeFoo()
  })
  test('should track async mutations', () => {
    expect.assertions(1)
    const app = createDefaultOvermind()
    app.eventHub.on(EventType.MUTATIONS, (data) => {
      expect(toJSON(data)).toEqual({
        actionId: 'waitAndChangeFoo',
        isRunning: true,
        actionName: 'waitAndChangeFoo',
        mutations: [
          {
            args: ['bar2'],
            method: 'set',
            path: 'foo',
            hasChangedValue: true,
            delimiter: '.'
          },
        ],
        executionId: 0,
        operatorId: 0,
        namespacePath: [],
        path: [],
        type: 'action',
      })
    })
    app.actions.waitAndChangeFoo()
  })
  test('should track async mutations with async await', () => {
    expect.assertions(1)
    const app = createDefaultOvermind()
    app.eventHub.on(EventType.MUTATIONS, (data) => {
      expect(toJSON(data)).toEqual({
        actionId: 'asyncChangeFoo',
        isRunning: true,
        actionName: 'asyncChangeFoo',
        mutations: [
          {
            args: ['bar2'],
            method: 'set',
            path: 'foo',
            hasChangedValue: true,
            delimiter: '.'
          },
        ],
        executionId: 0,
        operatorId: 0,
        path: [],
        namespacePath: [],
        type: 'action',
      })
    })
    app.actions.asyncChangeFoo()
  })
  test('should instantiate app with modules', () => {
    const foo = {
      state: {
        foo: 'bar',
      },
      actions: {
        foo() {},
      },
    }
    const bar = {
      state: {
        bar: 'baz',
      },
      effects: {
        hello: () => 'hello',
      },
      actions: {
        bar() {},
      },
    }

    const config = Object.assign(
      {},
      namespaced({
        foo,
        bar,
      })
    )

    const app = new Overmind(config)

    expect(app.state.foo.foo).toEqual('bar')
    expect(app.state.bar.bar).toEqual('baz')
    expect(typeof app.actions.foo.foo).toBe('function')
    expect(typeof app.actions.bar.bar).toBe('function')
  })
  test('should instantiate modules with onInitialize', () => {
    const result: string[] = []
    const app = new Overmind(
      namespaced({
        foo: {
          onInitialize: () => {
            result.push('foo')
          },
        },
        bar: {
          onInitialize: () => {
            result.push('bar')
          },
        },
      })
    )

    return app.initialized.then(() => {
      expect(result).toEqual(['foo', 'bar'])
    })
  })
  test('should allow mutations on passed values', () => {
    expect.assertions(2)
    const app = createDefaultOvermind()
    expect(() => app.actions.changeValue(app.state.item)).not.toThrow()
    expect(app.state.item.isAwesome).toBe(false)
  })
  test('should allow mutations on passed values in object', () => {
    expect.assertions(2)
    const app = createDefaultOvermind()
    expect(() =>
      app.actions.changeFormValue({
        form: app.state.item,
        key: 'isAwesome',
        value: false,
      })
    ).not.toThrow()
    expect(app.state.item.isAwesome).toBe(false)
  })
  test('should rehydrate mutations on hot reload', () => {
    expect.assertions(2)
    const app = createDefaultOvermind()
    app.actions.changeFoo()
    expect(app.state.foo).toBe('bar2')
    app.reconfigure({
      state: {
        foo: 'bar'
      }
    })
    expect(app.state.foo).toBe('bar2')
  })
  test('should rehydrate actions on hot reload', () => {
    expect.assertions(2)
    const app = createDefaultOvermind()
    const changeFoo = app.actions.changeFoo
    app.reconfigure({
      state: {
        foo: 'bar2'
      },
      actions: {
        changeFoo(context) {
          context.state.foo = 'replaced!'
        }
      }
    })

    expect(app.state.foo).toBe('bar2')
    changeFoo()
    expect(app.state.foo).toBe('replaced!')
  })
})

describe('Overmind mock', () => {
  test('should preserve getters', async (done) => {
    expect.assertions(1)
    const state = {
      value: 0,
      get valuePlusTwo() {
        return this.value + 2
      },
    }
    const updateValue: Action = (context) => {
      context.state.value = 15
    }
    const actions = { updateValue }
    const config = { state, actions }
    type Config = typeof config
    interface Action<Value = void> extends IAction<Config, Value> {}

    const mock = createOvermindMock(config)
    await mock.actions.updateValue()
    expect(mock.state.valuePlusTwo).toEqual(17)
    done()
  })
})
