import Overmind, { Action, TAction, TApp, modules } from './'

describe('Overmind', () => {
  test('should instantiate app with state', () => {
    const app = new Overmind({
      state: {
        foo: 'bar',
      },
      actions: {
        doThis: (action) => action.run(() => {}),
      },
      effects: {},
    })

    expect(app.state.foo).toEqual('bar')
  })
  test('should instantiate app with onInitialize', () => {
    interface AppType {
      state: {
        foo: string
      }
      actions: {
        doThis(): void
      }
    }
    const onInitialize: Action<AppType, string> = (action) =>
      action.map(({ value }) => {
        expect(value.state.foo).toBe('bar')
        expect(typeof value.actions.doThis === 'function')
        return 'foo'
      })
    const app = new Overmind({
      onInitialize,
      state: {
        foo: 'bar',
      },
      actions: {
        doThis: (action) => action.run(() => {}),
      },
      effects: {},
    })

    return app.initialized.then((value) => {
      expect(value).toEqual('foo')
    })
  })
  test('should instantiate app with modules', () => {
    const fooAction: Action<string> = ({ run }) => run(() => {})

    const foo = {
      state: {
        foo: 'bar',
      },
      actions: {
        foo: fooAction,
      },
    }
    const bar = {
      state: {
        bar: 'baz',
      },
      effects: {
        hello: () => 'hello',
      },
      actions: {
        bar: fooAction,
      },
    }

    const config = modules({
      modules: {
        foo,
        bar,
      },
    })

    type App = TApp<{
      state: {
        foo: typeof config.state.foo
        bar: typeof config.state.bar
      }
      effects: {
        bar: typeof config.effects.bar
      }
      actions: {
        foo: typeof config.actions.foo
        bar: typeof config.actions.bar
      }
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>

    const app = new Overmind(config)

    expect(app.state.foo.foo).toEqual('bar')
    expect(app.state.bar.bar).toEqual('baz')
    expect(app.actions.foo.foo('mip')).toEqual('mip')
    expect(app.actions.bar.bar('bop')).toEqual('bop')
  })
  test('should instantiate modules with onInitialize', () => {
    const result = []
    const app = new Overmind(
      modules({
        modules: {
          foo: {
            onInitialize: (action) => () => {
              result.push('foo')
            },
          },
          bar: {
            onInitialize: (action) => () => {
              result.push('bar')
            },
          },
        },
      })
    )

    return app.initialized.then(() => {
      expect(result).toEqual(['foo', 'bar'])
    })
  })
})

describe('OPERATORS', () => {
  test('fork', () => {
    expect.assertions(2)
    let calledFoo = false
    const doThis: Action = ({ fork }) =>
      fork(() => 'foo', {
        foo: ({ run }) => run(() => (calledFoo = true)).map(() => '123'),
        bar: ({ map }) => map(() => 123),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>

    const app = new Overmind(config)

    expect(app.actions.doThis()).toBe(undefined)
    expect(calledFoo).toBe(true)
  })
  test('mutate', () => {
    const doThis: Action = ({ mutate }) =>
      mutate(({ state }) => (state.foo = 'bar2'))
    const config = {
      state: {
        foo: 'bar',
      },
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      state: {
        foo: string
      }
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>

    const app = new Overmind(config)

    expect(app.state.foo).toEqual('bar')
    app.actions.doThis()
    expect(app.state.foo).toEqual('bar2')
  })
  test('do', () => {
    expect.assertions(2)
    const doThis: Action<string> = ({ run }) =>
      run(({ foo }) => {
        expect(foo.bar()).toBe('baz')
        return Promise.resolve('bihihi')
      })
    const config = {
      state: {
        foo: 'bar',
      },
      effects: {
        foo: {
          bar: () => 'baz',
        },
      },
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      state: {
        foo: string
      }
      effects: {
        foo: typeof config.effects.foo
      }
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>

    const app = new Overmind(config)
    return app.actions
      .doThis('foo')
      .then((result) => expect(result).toBe('foo'))
  })
  test('map', () => {
    expect.assertions(1)
    const doThis: Action<string> = ({ map }) =>
      map(({ value }) => {
        return value.toUpperCase()
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>

    const app = new Overmind(config)

    expect(app.actions.doThis('foo')).toBe('FOO')
  })

  test('try - resolved', () => {
    expect.assertions(1)
    const doThis: Action = ({ attempt }) =>
      attempt(() => Promise.resolve(), {
        success: ({ map }) => map(() => 'foo'),
        error: ({ map }) => map(() => 'error'),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)

    return Promise.resolve(app.actions.doThis()).then((value) => {
      expect(value).toBe('foo')
    })
  })

  test('try - rejected', () => {
    expect.assertions(1)
    const doThis: Action = ({ attempt }) =>
      attempt(() => Promise.reject(new Error()), {
        success: ({ map }) => map(() => 'foo'),
        error: ({ map }) => map(() => 'bar'),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)
    return Promise.resolve(app.actions.doThis()).then((value) => {
      expect(value).toBe('bar')
    })
  })
  test('when - true', () => {
    expect.assertions(1)
    const doThis: Action = ({ when }) =>
      when(() => true, {
        true: ({ map }) => map(() => 'foo'),
        false: ({ run }) => run(() => {}),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)
    expect(app.actions.doThis()).toBe('foo')
  })

  test('when - false', () => {
    expect.assertions(1)
    const doThis: Action = ({ when }) =>
      when(() => false, {
        true: ({ map }) => map(() => 'foo'),
        false: ({ map }) => map(() => 'bar'),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)

    expect(app.actions.doThis()).toBe('bar')
  })

  test('filter - true', () => {
    expect.assertions(1)
    const doThis: Action<string> = ({ filter }) =>
      filter(() => true).map(() => 'bar')
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)
    expect(app.actions.doThis('foo')).toBe('bar')
  })
  test('filter - false', () => {
    expect.assertions(1)
    const doThis: Action<string> = ({ filter }) =>
      filter(() => false).map(() => 'bar')
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)

    expect(app.actions.doThis('foo')).toEqual({ value: 'foo' })
  })
  test('debounce', () => {
    expect.assertions(2)
    const start = Date.now()
    let end
    const doThis: Action = ({ debounce }) =>
      debounce(100)
        .run(() => {
          end = Date.now()
        })
        .map(() => 'foo')
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)

    return Promise.resolve(app.actions.doThis()).then((value) => {
      expect(value).toBe('foo')
      expect(end - start).toBeGreaterThanOrEqual(100)
    })
  })
  test('compose', () => {
    const mockCallback = jest.fn()
    const runMock: Action = ({ run }) =>
      run(() => {
        mockCallback()
      })
    const doThis: Action = ({ compose }) => compose(runMock)
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)
    app.actions.doThis()
    expect(mockCallback.mock.calls.length).toBe(1)
  })
  test('parallel', () => {
    const mockCallback = jest.fn()
    const runMock: Action = ({ run }) =>
      run(() => {
        mockCallback()
      })
    const doThis: Action = ({ parallel }) => parallel([runMock])
    const config = {
      actions: {
        doThis,
      },
    }
    type App = TApp<{
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<App, Input, Output>
    const app = new Overmind(config)
    app.actions.doThis()
    expect(mockCallback.mock.calls.length).toBe(1)
  })
})
