import { ResolveActions, ResolveState, TBaseContext } from './internalTypes'
import { Overmind } from './'

/** ===== PUBLIC API
 */
export { EventType } from './internalTypes'

export type Configuration = {
  onInitialize?: any
  state?: {}
  effects?: {}
  actions?: {}
}

export type BaseApp = {
  state: {}
  effects: {}
  actions: {}
}

export type TStateObject =
  | {
      [key: string]:
        | TStateObject
        | string
        | IDerive<any, any, any>
        | number
        | boolean
        | object
    }
  | undefined

export interface IConfig<Config extends Configuration> {
  state: Config['state'] & {}
  actions: Config['actions'] & {}
  effects: Config['effects'] & {}
}

// This is the type of the `app` argument passed in components.
export type TApp<Config extends Configuration> = {
  // Resolves `Derive` types in state.
  state: ResolveState<Config['state']>
  actions: ResolveActions<Config['actions']>
  effects: Config['effects']
}

// This is the type of the argument passed in actions.
export type TValueContext<Config extends Configuration, Value> = TBaseContext<
  Config
> & {
  value: Value
}

export interface IAction<Config extends Configuration, Value> {
  (context: TValueContext<Config, Value>): any
}

export type IOperator<Config extends Configuration, Input, Output = Input> = (
  err: Error | null,
  val: TValueContext<Config, Input>,
  next: (err: Error | null, val?: TValueContext<Config, Output>) => void,
  final?: (err, Error, val?: TValueContext<Config, Output>) => void
) => void

export type IDerive<
  Config extends Configuration,
  Parent extends TStateObject,
  Value
> = (
  parent: ResolveState<Parent>,
  state: ResolveState<Config['state'] & {}>
) => Value

export interface IOnInitialize<Config extends Configuration> {
  (context: TValueContext<Config, Overmind<Config>>): void
}
